var player = {
                       code : 2,
                      name : 'Tom',
                     status : 'active',
                      score : [
                                    7,
                                    5,
                                    9,
                                    7
                                  ],
            get_average:function(){
                                    var score = this.score;
                                    //注:this代指player对象本身(可以认为this的实际值就是player对象,具体用法将在下一节内容介绍)
                                    var length = score.length;
                                    var totle = 0;
                                    for(var i=0;i<length;i++){
                                        totle += score[i];
                                    }
                                    return totle/length;
                              }
                     }
console.log( player.get_average() );
